import React, { Component } from 'react'
import axios from 'axios'
class PostMethod extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            userId : '',
            title : '',
            body : ''
        }
    }
    

    render() {
        const {userId,title,body} = this.state
        handleSubmit = (event) =>{
            event.preventDefault()
            axios.post('https://jsonplaceholder.typicode.com/',{
                userId,title,body
            }).then((res)=>{console.log(res)}).catch(error => console.log(error) )
        }

        onChangeHandle =  (e) =>{
            this.setState({
                [e.target.name] : e.target.value
            })
        }

        return (
            <div>
                <form onSubmit={this.handleSubmit} >
                    <input onChange={this.onChangeHandle} name="userId" value={this.state.userId} />
                    <input onChange={this.onChangeHandle} name="title" value={this.state.userId} />
                    <input onChange={this.onChangeHandle} name="body" value={this.state.userId} />
                    <button type="submit" >send data by axios</button>
                </form>
            </div>
        )
    }
}

export default PostMethod
