import React, { Component } from 'react'
import Persons from '../components/ListPersonsService'
import Option from './SelectOption'
class Form extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             userName : '',
             topic : 'select value'
        }
    }

    onUserNameChange = (event) => {
        this.setState({
            userName : event.target.value
        },()=>{
            console.log(this.state.userName);
        })
    }
    onSelect =(event) =>{
        this.setState({
            topic : event.target.value
        })
    }
    

    render() {
        const options = Persons.map(person =><Option click={this.onSelect} key={person._id} value={person.name} name={person.email} />)
        return (
            <div>
                <form>
                    <label>user name </label>
                    <input value={this.state.userName} onChange={this.onUserNameChange} />
                    <select>
                        {options}
                    </select>
                </form>
            </div>
        )
    }
}

export default Form
